load("./Grouped_objects/metadata_mac20_1_3sig.rd")
colnames(metadata)<-gsub("data_Siggrp","MacSig",colnames(metadata))
metadata$batch<-gsub("Mac(\\d)(\\d)[.].*","Mac\\1\\2",rownames(metadata))
load("./Grouped_objects/data_counts_mac20_1_3.rd")
load("./Grouped_objects/data_logcpm_mac20_1_3.rd")


allsum<-Rfast::colsums(l.data.cpm)  
col_pal<-c("GM"="#008000","GM_TNF"="#008000","GM_IFNg"="#40B740","GM_TNF_IFNg"="#40B740","GM_(TNF)_IFNg"="#40B740",
           "GM_LPS"="#FF4A42","GM_LPS_TNF"="#FF4A42","GM_LPS_IFNg"="#8E4700","GM_LPS_TNF_IFNg"="#8E4700",
           "GM_IL4"="#06677A","GM_TNF_IL4"="#06677A","GM_(TNF)_IL4"="#06677A","GM_IL4_IFNg"="#11A9C4","GM_TNF_IL4_IFNg"="#11A9C4",
           "GM_LPS_IL4"="#2929A0","GM_LPS_TNF_IL4"="#2929A0","GM_LPS_IL4_IFNg"="#7070E0","GM_LPS_TNF_IL4_IFNg"="#7070E0" )
metadata$condition<-factor(metadata$condition, levels=names(col_pal))

tl_data<-as.data.frame(as.matrix(t(l.data.cpm)))
# metadata$condition[match(rownames(minidf),rownames(metadata))] # Je sais plus l'ordre pour match, mais c'est le m?me donc autant faire :
merged_df <- merge(metadata[,c(1:2,8:ncol(metadata))], tl_data, by = "row.names")   # don't keep LPS/TNF/GM detail as it might interfer with genes
rownames(merged_df) <- merged_df$Row.names
merged_df <- merged_df[, -1] 
merged_df_complete<-merged_df


Make_prism_barplot<-function(merged_df,title_name){
  df_sum <- merged_df %>%
    group_by(condition) %>%
    summarise(mean  = mean(i_y2, na.rm = TRUE),
              se    = sd(i_y2, na.rm = TRUE) / sqrt(sum(!is.na(i_y2))),
              upper = mean + se, .groups = "drop"  )
  
  ggplot() + geom_col( data = df_sum,
                       aes(x = condition, y = mean, fill = condition, color = condition),
                       alpha = 1,width = 0.8,size=1,
                       position = position_dodge(width = 0.8)) +scale_color_manual(values = rep("black",7))+
    
    geom_jitter(  data = merged_df,size = 2,
                  aes(x = condition, y = i_y2, color = condition ), # shape = batch, 
                  position = position_jitterdodge(
                    jitter.width = 0.20,jitter.height = 0,dodge.width = 0.8 )) +
    geom_errorbar(data = df_sum, aes(x = condition, ymin = mean, ymax = upper), width = 0.4, size = rel(1))+
    scale_fill_manual(values = palette2) +
    scale_x_discrete(limits = ord_x3, labels= labels) +
    ggtitle(title_name) + 
    ggprism::theme_prism(base_size = 16)+ labs(y= title_name)+
    theme(legend.position = "none",axis.title.x =element_blank(),axis.title.y =element_blank(),
          axis.text.y = element_text(size=rel(1.5),margin = margin(0,8,0,0, unit = "points")),
          axis.text.x = element_text(size=rel(1),angle=90,hjust=1,vjust=0.5),
          axis.ticks.length = unit(15,"points"),
          plot.margin = unit(c(20, 4, 4, 4),"points"))
  
}


Make_prism_boxplot<-function(merged_df, title_name){
  ggplot(merged_df, aes(x =condition, y =i_y2, fill=condition))+ 
    geom_hline(yintercept=0, linetype="dashed", color = "lightgrey",size=rel(1))+
    stat_boxplot(geom = "errorbar", width = 0.3,size=1) +
    geom_boxplot(lwd=1.15, color="black", fatten = NULL, outlier.colour = "white",width=0.6)+
    scale_x_discrete(limits = levels(merged_df$condition) , labels= labels)+
    scale_fill_manual(values = palette2)+ scale_y_continuous(n.breaks = 5) +
    ggprism::theme_prism(base_size = 16)+ ggtitle(title_name) + 
    stat_summary(fun = median,geom = "crossbar",width = 0.57, fatten = 2)+ 
    geom_jitter(shape=21, position=position_jitter(width = 0.20),size=3, fill="white",stroke=1.5) +
    theme(legend.position = "none",axis.title.x =element_blank(),axis.title.y =element_blank(),
          axis.text.y = element_text(size=rel(1.5),margin = margin(0,8,0,0, unit = "points")),
          axis.text.x = element_text(size=rel(1),angle=90,hjust=1,vjust=0.5),
          axis.ticks.length = unit(15,"points"),
          plot.margin = unit(c(20, 4, 4, 4),"points"))
  
}







ord_x3=c("GM","GM_TNF","GM_IFNg","GM_TNF_IFNg","nothing",
         "GM_LPS","GM_LPS_TNF","GM_LPS_IFNg","GM_LPS_TNF_IFNg") 
palette2<-c("GM"="#E2F0DA","GM_TNF"="#C6DFB4","GM_IFNg"="#A9D08E","GM_TNF_IFNg"="#548335","nothing"="#black",
            "GM_LPS"="#FCE4D6","GM_LPS_TNF"="#F8CBAD","GM_LPS_IFNg"="#F4B085","GM_LPS_TNF_IFNg"="#C65911")
labels=c("GM","GM_TNF","GM_IFNg","GM_TNF_IFNg","",
         "GM_LPS","GM_LPS_TNF","GM_LPS_IFNg","GM_LPS_TNF_IFNg")

#Remove samples not needed/badly sequenced/with experimental problems
rm_2<-"Mac22.13,Mac22.6,Mac20.3.4,Mac20.5.6,Mac22.12,Mac21.1,Mac21.2,Mac21.3,Mac21.4,Mac21.5,Mac21.6,Mac21.7,Mac21.8,Mac21.9,Mac21.10,Mac21.11,Mac21.12,Mac21.13,Mac21.14,Mac21.15,Mac21.16"
i_rm2<-unlist(strsplit(rm_2,split = ","))
merged_df<-merged_df_complete[setdiff(rownames(merged_df_complete),i_rm2),]
merged_df2<-merged_df[which(merged_df$condition%in%labels),]
merged_df2$condition<-factor(merged_df2$condition, levels = labels)
merged_df2$i_y2<-NA




Make_prism_plot<-function(brbx_y2,title_name){
  i_y2<-unlist(strsplit(brbx_y2,split = ","))
  if(length(i_y2)<2){
    eval(parse(text=paste0("merged_df2$i_y2<-merged_df2[,i_y2]")))          # drop=F to keep the name of the column
    Make_prism_barplot(merged_df=merged_df2,title_name=title_name)
    
  }else if(length(i_y2)>=2){
    gnshow_y<-gsub(" ","",i_y2) 
    notfound_y<-setdiff(gnshow_y,rownames(l.data.cpm))
    gnshow_y2<-setdiff(gnshow_y,notfound_y)
    currentscore<-(Rfast::colsums(l.data.cpm[gnshow_y2,])/allsum )
    names(currentscore)<-colnames(l.data.cpm)
    currentscore<-currentscore[setdiff(names(currentscore),i_rm2)]
    crt_sc<-as.vector(scale(currentscore, center = TRUE, scale = TRUE))
    names(crt_sc)<-names(currentscore)
    
    eval(parse(text=paste0("merged_df2$i_y2<-crt_sc[match(rownames(merged_df2),names(crt_sc))]")))
    Make_prism_boxplot(merged_df=merged_df2,title_name=title_name)
  }
}


